"use client"

import { useState, useEffect } from "react"
import Navbar from "../components/Navbar"
import axios from "axios"
import { FaPlus, FaCheck, FaTimes } from "react-icons/fa"
import DatePicker from "react-datepicker"
import "react-datepicker/dist/react-datepicker.css"

const Marketplace = () => {
  const [crops, setCrops] = useState([])
  const [loading, setLoading] = useState(true)
  const [showForm, setShowForm] = useState(false)
  const [formData, setFormData] = useState({
    cropName: "",
    Maxprice: "",
    Minprice: "",
    Avgprice: "",
    location: "",
    date: new Date(),
  })
  const [previewData, setPreviewData] = useState(null)
  const [error, setError] = useState("")
  const [success, setSuccess] = useState("")
  const [user, setUser] = useState({ name: "Admin" })
  const [expandedDates, setExpandedDates] = useState({})

  useEffect(() => {
    fetchCrops()
  }, [])

  const fetchCrops = async () => {
    try {
      setLoading(true)
      const response = await axios.get("http://localhost:5000/api/crops")
      setCrops(response.data)
      setLoading(false)
    } catch (error) {
      console.error("Error fetching crops:", error)
      setLoading(false)
    }
  }

  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData({
      ...formData,
      [name]: value,
    })
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    setError("")

    if (!formData.cropName || !formData.Maxprice || !formData.Minprice || !formData.Avgprice || !formData.location) {
      setError("Please fill in all fields")
      return
    }

    setPreviewData({
      ...formData,
      Maxprice: Number.parseFloat(formData.Maxprice),
      Minprice: Number.parseFloat(formData.Minprice),
      Avgprice: Number.parseFloat(formData.Avgprice),
      date: formData.date.toISOString().split("T")[0], // store as YYYY-MM-DD
      admin: { name: user?.name || "Unknown" },
    })

    setShowForm(false)
  }

  const confirmSubmission = async () => {
    try {
      await axios.post("http://localhost:5000/api/crops", previewData)
      setSuccess("Crop rate added successfully!")
      setPreviewData(null)
      setFormData({
        cropName: "",
        Maxprice: "",
        Minprice: "",
        Avgprice: "",
        location: "",
        date: new Date(),
      })
      fetchCrops()
      setTimeout(() => setSuccess(""), 3000)
    } catch (error) {
      console.error("Error adding crop rate:", error)
      setError("Failed to add crop rate. Please try again.")
      setPreviewData(null)
    }
  }

  const cancelSubmission = () => {
    setPreviewData(null)
    setShowForm(true)
  }

  const toggleDate = (date) => {
    setExpandedDates((prev) => ({
      ...prev,
      [date]: !prev[date],
    }))
  }

  const formatDate = (date) => {
    const d = new Date(date)
    const day = String(d.getDate()).padStart(2, "0")
    const month = String(d.getMonth() + 1).padStart(2, "0")
    const year = d.getFullYear()
    return `${day}/${month}/${year}`
  }

  return (
    <div className="min-h-screen bg-green-50">
      <Navbar />
      <div className="container mx-auto px-4 py-8">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-2xl md:text-3xl font-bold text-gray-800">Marketplace</h1>
          {!showForm && !previewData && (
            <button
              onClick={() => setShowForm(true)}
              className="bg-green-700 hover:bg-green-600 text-white font-bold py-2 px-4 rounded-lg flex items-center transition duration-300"
            >
              <FaPlus className="mr-2" /> Add Crop Rate
            </button>
          )}
        </div>

        {success && (
          <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">{success}</div>
        )}
        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">{error}</div>
        )}

        {showForm && (
          <div className="bg-white rounded-lg shadow-md p-6 mb-8">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">Add New Crop Rate</h2>
            <form onSubmit={handleSubmit}>
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label className="block mb-2 text-sm font-bold text-gray-700">Crop Name</label>
                  <input
                    name="cropName"
                    type="text"
                    className="w-full border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-green-500"
                    placeholder="e.g., Rice"
                    value={formData.cropName}
                    onChange={handleChange}
                  />
                </div>
                <div>
                  <label className="block mb-2 text-sm font-bold text-gray-700">MaxPrice</label>
                  <input
                    name="Maxprice"
                    type="number"
                    step="0.01"
                    className="w-full border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-green-500"
                    placeholder="e.g., 25.50"
                    value={formData.Maxprice}
                    onChange={handleChange}
                  />
                  <label className="block mb-2 text-sm font-bold text-gray-700">MinPrice</label>
                  <input
                    name="Minprice"
                    type="number"
                    step="0.01"
                    className="w-full border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-green-500"
                    placeholder="e.g., 18.00"
                    value={formData.Minprice}
                    onChange={handleChange}
                  />
                  <label className="block mb-2 text-sm font-bold text-gray-700">AvgPrice</label>
                  <input
                    name="Avgprice"
                    type="number"
                    step="0.01"
                    className="w-full border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-green-500"
                    placeholder="e.g., 22.75"
                    value={formData.Avgprice}
                    onChange={handleChange}
                  />
                </div>
                <div>
                  <label className="block mb-2 text-sm font-bold text-gray-700">Location</label>
                  <input
                    name="location"
                    type="text"
                    className="w-full border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-green-500"
                    placeholder="e.g., Pune"
                    value={formData.location}
                    onChange={handleChange}
                  />
                </div>
                <div>
                  <label className="block mb-2 text-sm font-bold text-gray-700">Date</label>
                  <DatePicker
                    selected={formData.date}
                    onChange={(date) => setFormData({ ...formData, date })}
                    dateFormat="dd/MM/yyyy"
                    className="w-full border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-green-500"
                  />
                </div>
              </div>
              <div className="flex justify-between mt-6">
                <button
                  type="button"
                  onClick={() => setShowForm(false)}
                  className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="bg-green-700 hover:bg-green-600 text-white font-bold py-2 px-4 rounded"
                >
                  Preview
                </button>
              </div>
            </form>
          </div>
        )}

        {previewData && (
          <div className="bg-white rounded-lg shadow-md p-6 mb-8 border-2 border-green-500">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">Confirm Crop Rate Details</h2>
            <div className="grid md:grid-cols-2 gap-4 mb-6">
              <div><p className="text-sm text-gray-500">Crop</p><p className="font-medium">{previewData.cropName}</p></div>
              <div><p className="text-sm text-gray-500">MaxPrice</p><p className="font-medium">₹{previewData.Maxprice}</p></div>
              <div><p className="text-sm text-gray-500">MinPrice</p><p className="font-medium">₹{previewData.Minprice}</p></div>
              <div><p className="text-sm text-gray-500">AvgPrice</p><p className="font-medium">₹{previewData.Avgprice}</p></div>
              <div><p className="text-sm text-gray-500">Location</p><p className="font-medium">{previewData.location}</p></div>
              <div><p className="text-sm text-gray-500">Date</p><p className="font-medium">{formatDate(previewData.date)}</p></div>
              <div><p className="text-sm text-gray-500">Added By</p><p className="font-medium">{previewData.admin?.name || "Unknown"}</p></div>
            </div>
            <div className="flex justify-between">
              <button
                onClick={cancelSubmission}
                className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded flex items-center"
              >
                <FaTimes className="mr-2" /> Cancel
              </button>
              <button
                onClick={confirmSubmission}
                className="bg-green-700 hover:bg-green-600 text-white font-bold py-2 px-4 rounded flex items-center"
              >
                <FaCheck className="mr-2" /> Confirm & Save
              </button>
            </div>
          </div>
        )}

        {/* Crop Table */}
        <div className="bg-white rounded-lg shadow-md overflow-hidden">
          <h2 className="text-xl font-semibold text-gray-800 p-6 border-b">Current Crop Rates</h2>
          {loading ? (
            <div className="p-6 text-center">Loading crop rates...</div>
          ) : crops.length === 0 ? (
            <div className="p-6 text-center">No crop rates available.</div>
          ) : (
            <div className="divide-y">
              {Object.entries(
                crops.reduce((grouped, crop) => {
                  const date = new Date(crop.date).toLocaleDateString()
                  if (!grouped[date]) grouped[date] = []
                  grouped[date].push(crop)
                  return grouped
                }, {})
              ).map(([date, cropsOnDate]) => (
                <div key={date} className="border-b">
                  <button
                    onClick={() => toggleDate(date)}
                    className="w-full text-left px-6 py-4 bg-green-100 hover:bg-green-200 font-semibold text-green-800 flex justify-between items-center"
                  >
                    <span>{date}</span>
                    <span>{expandedDates[date] ? "▲" : "▼"}</span>
                  </button>
                  {expandedDates[date] && (
                    <div className="overflow-x-auto">
                      <table className="w-full">
                        <thead className="bg-green-50">
                          <tr>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Crop</th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">MaxPrice</th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">MinPrice</th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">AvgPrice</th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Location</th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Added By</th>
                          </tr>
                        </thead>
                        <tbody className="divide-y divide-gray-200">
                          {cropsOnDate.map((crop) => (
                            <tr key={crop._id} className="hover:bg-green-50">
                              <td className="px-6 py-4 whitespace-nowrap">{crop.cropName}</td>
                              <td className="px-6 py-4 whitespace-nowrap">₹{crop.Maxprice}</td>
                              <td className="px-6 py-4 whitespace-nowrap">₹{crop.Minprice}</td>
                              <td className="px-6 py-4 whitespace-nowrap">₹{crop.Avgprice}</td>
                              <td className="px-6 py-4 whitespace-nowrap">{crop.location}</td>
                              <td className="px-6 py-4 whitespace-nowrap">{crop.admin?.name || "Unknown"}</td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  )
}

export default Marketplace
