"use client"

import { useState, useEffect } from "react"
import Navbar from "../components/Navbar"
import axios from "axios"
import { FaPlus, FaCheck, FaTimes } from "react-icons/fa"

const Marketplace = () => {
  const [crops, setCrops] = useState([])
  const [loading, setLoading] = useState(true)
  const [showForm, setShowForm] = useState(false)
  const [formData, setFormData] = useState({
    cropName: "",
    Maxprice: "",
    Minprice: "",
    Avgprice:"",
    location: "",
    date: new Date().toISOString().split("T")[0],
  })
  const [previewData, setPreviewData] = useState(null)
  const [error, setError] = useState("")
  const [success, setSuccess] = useState("")

  // Get token from localStorage for API requests
  const getAuthHeader = () => {
    const token = localStorage.getItem("token")
    return token ? { Authorization: `Bearer ${token}` } : {}
  }

  useEffect(() => {
    fetchCrops()
  }, [])

  const fetchCrops = async () => {
    try {
      setLoading(true)
      const response = await axios.get("http://localhost:5000/api/crops")
      setCrops(response.data.data || response.data) // Handle both formats
      setLoading(false)
    } catch (error) {
      console.error("Error fetching crops:", error)
      setLoading(false)
    }
  }

  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData({
      ...formData,
      [name]: value,
    })
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    setError("")

    // Validate form
    if (!formData.cropName || !formData.Maxprice || !formData.Minprice || !formData.Avgprice|| !formData.location) {
      setError("Please fill in all fields")
      return
    }

    // Show preview
    setPreviewData({
      ...formData,
      Maxprice: Number.parseFloat(formData.Maxprice),
      Minprice: Number.parseFloat(formData.Minprice),
      Avgprice: Number.parseFloat(formData.Avgprice),

    })
    setShowForm(false)
  }

  const confirmSubmission = async () => {
    try {
      // Make sure we're sending the correct data format
      const dataToSubmit = {
        cropName: previewData.cropName,
        price: previewData.price,
        location: previewData.location,
        date: previewData.date,
      }

      // Include auth token in the request
      const response = await axios.post("http://localhost:5000/api/crops", dataToSubmit, {
        headers: getAuthHeader(),
      })

      if (response.data && (response.data.success || response.status === 201)) {
        setSuccess("Crop rate added successfully!")
        setPreviewData(null)
        setFormData({
          cropName: "",
          price: "",
          location: "",
          date: new Date().toISOString().split("T")[0],
        })
        fetchCrops() // Refresh the crop list

        // Clear success message after 3 seconds
        setTimeout(() => {
          setSuccess("")
        }, 3000)
      } else {
        throw new Error("Failed to add crop rate")
      }
    } catch (error) {
      console.error("Error adding crop rate:", error)
      setError("Failed to add crop rate. Please try again.")
      setPreviewData(null)
    }
  }

  const cancelSubmission = () => {
    setPreviewData(null)
    setShowForm(true)
  }

  return (
    <div className="min-h-screen bg-green-50">
      <Navbar />

      <div className="container mx-auto px-4 py-8">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-2xl md:text-3xl font-bold text-gray-800">Marketplace</h1>

          {!showForm && !previewData && (
            <button
              onClick={() => setShowForm(true)}
              className="bg-green-700 hover:bg-green-600 text-white font-bold py-2 px-4 rounded-lg flex items-center transition duration-300"
            >
              <FaPlus className="mr-2" /> Add Crop Rate
            </button>
          )}
        </div>

        {/* Success Message */}
        {success && (
          <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">{success}</div>
        )}

        {/* Error Message */}
        {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">{error}</div>}

        {/* Add Crop Rate Form */}
        {showForm && (
          <div className="bg-white rounded-lg shadow-md p-6 mb-8">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">Add New Crop Rate</h2>

            <form onSubmit={handleSubmit}>
              <div className="grid md:grid-cols-2 gap-4">
                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="cropName">
                    Crop Name
                  </label>
                  <input
                    id="cropName"
                    name="cropName"
                    type="text"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline focus:border-green-500"
                    placeholder="e.g., Rice, Wheat, Cotton"
                    value={formData.cropName}
                    onChange={handleChange}
                  />
                </div>

                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="price">
                    Price (per kg/quintal)
                  </label>
                  <input
                    id="price"
                    name="price"
                    type="number"
                    step="0.01"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline focus:border-green-500"
                    placeholder="e.g., 25.50"
                    value={formData.price}
                    onChange={handleChange}
                  />
                </div>

                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="location">
                    Location
                  </label>
                  <input
                    id="location"
                    name="location"
                    type="text"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline focus:border-green-500"
                    placeholder="e.g., Pune, Maharashtra"
                    value={formData.location}
                    onChange={handleChange}
                  />
                </div>

                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="date">
                    Date
                  </label>
                  <input
                    id="date"
                    name="date"
                    type="date"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline focus:border-green-500"
                    value={formData.date}
                    onChange={handleChange}
                  />
                </div>
              </div>

              <div className="flex items-center justify-between mt-4">
                <button
                  type="button"
                  onClick={() => setShowForm(false)}
                  className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline transition duration-300"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="bg-green-700 hover:bg-green-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline transition duration-300"
                >
                  Preview
                </button>
              </div>
            </form>
          </div>
        )}

        {/* Preview Confirmation */}
        {previewData && (
          <div className="bg-white rounded-lg shadow-md p-6 mb-8 border-2 border-green-500">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">Confirm Crop Rate Details</h2>

            <div className="grid md:grid-cols-2 gap-4 mb-6">
              <div>
                <p className="text-sm text-gray-500">Crop Name</p>
                <p className="font-medium">{previewData.cropName}</p>
              </div>

              <div>
                <p className="text-sm text-gray-500">Price</p>
                <p className="font-medium">₹{previewData.price}</p>
              </div>

              <div>
                <p className="text-sm text-gray-500">Location</p>
                <p className="font-medium">{previewData.location}</p>
              </div>

              <div>
                <p className="text-sm text-gray-500">Date</p>
                <p className="font-medium">{new Date(previewData.date).toLocaleDateString()}</p>
              </div>
            </div>

            <div className="flex items-center justify-between">
              <button
                onClick={cancelSubmission}
                className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded flex items-center focus:outline-none focus:shadow-outline transition duration-300"
              >
                <FaTimes className="mr-2" /> Cancel
              </button>
              <button
                onClick={confirmSubmission}
                className="bg-green-700 hover:bg-green-600 text-white font-bold py-2 px-4 rounded flex items-center focus:outline-none focus:shadow-outline transition duration-300"
              >
                <FaCheck className="mr-2" /> Confirm & Save
              </button>
            </div>
          </div>
        )}

        {/* Crop Rates Table */}
        <div className="bg-white rounded-lg shadow-md overflow-hidden">
          <h2 className="text-xl font-semibold text-gray-800 p-6 border-b">Current Crop Rates</h2>

          {loading ? (
            <div className="p-6 text-center">
              <p>Loading crop rates...</p>
            </div>
          ) : crops.length === 0 ? (
            <div className="p-6 text-center">
              <p>No crop rates available. Add your first crop rate!</p>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-green-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Crop
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Price
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Location
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Date
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Added By
                    </th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-200">
                  {crops.map((crop) => (
                    <tr key={crop._id || crop.id} className="hover:bg-green-50">
                      <td className="px-6 py-4 whitespace-nowrap">{crop.cropName}</td>
                      <td className="px-6 py-4 whitespace-nowrap">₹{crop.price}</td>
                      <td className="px-6 py-4 whitespace-nowrap">{crop.location}</td>
                      <td className="px-6 py-4 whitespace-nowrap">{new Date(crop.date).toLocaleDateString()}</td>
                      <td className="px-6 py-4 whitespace-nowrap">{crop.admin?.name || "Unknown"}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  )
}

export default Marketplace
