"use client"

import { useState, useEffect } from "react"
import Navbar from "../components/Navbar"
import axios from "axios"
import { FaPlus, FaCheck, FaTimes } from "react-icons/fa"

const Marketplace = () => {
  const [crops, setCrops] = useState([])
  const [loading, setLoading] = useState(true)
  const [showForm, setShowForm] = useState(false)
  const [formData, setFormData] = useState({
    cropName: "",
    Maxprice: "",
    Minprice: "",
    Avgprice: "",
    location: "",
    date: new Date().toISOString().split("T")[0],
  })
  const [previewData, setPreviewData] = useState(null)
  const [error, setError] = useState("")
  const [success, setSuccess] = useState("")
  const [submitting, setSubmitting] = useState(false)
  const [user, setUser] = useState({ name: "Admin" }) // mock user name
  const [expandedDates, setExpandedDates] = useState({})

  useEffect(() => {
    fetchCrops()
  }, [])

  const fetchCrops = async () => {
    try {
      setLoading(true)
      const response = await axios.get("http://localhost:3000/api/crops", {
        headers: {
          Accept: "application/json",
        },
      })
      setCrops(response.data)
      setLoading(false)
    } catch (error) {
      console.error("Error fetching crops:", error)
      setLoading(false)
    }
  }

  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData({
      ...formData,
      [name]: value,
    })
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    setError("")

    const { cropName, Maxprice, Minprice, Avgprice, location } = formData

    if (!cropName || !Maxprice || !Minprice || !Avgprice || !location) {
      setError("Please fill in all fields")
      return
    }

    setPreviewData({
      ...formData,
      Maxprice: parseFloat(Maxprice),
      Minprice: parseFloat(Minprice),
      Avgprice: parseFloat(Avgprice),
      date: new Date(formData.date),
      admin: { name: user.name },
    })

    setShowForm(false)
  }

  const confirmSubmission = async () => {
    setSubmitting(true)
    try {
      // REPLACE this token with a real one after login
      const token = "mocked-auth-token"

      await axios.post("http://localhost:3000/api/crops", previewData, {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      })

      setSuccess("Crop rate added successfully!")
      setPreviewData(null)
      setFormData({
        cropName: "",
        Maxprice: "",
        Minprice: "",
        Avgprice: "",
        location: "",
        date: new Date().toISOString().split("T")[0],
      })
      fetchCrops()
    } catch (error) {
      console.error("Error adding crop rate:", error)
      setError("Failed to add crop rate. Please try again.")
    }
    setSubmitting(false)
  }

  const cancelSubmission = () => {
    setPreviewData(null)
    setShowForm(true)
  }

  const handleCancelForm = () => {
    setShowForm(false)
    setError("")
  }

  const toggleDate = (date) => {
    setExpandedDates((prev) => ({
      ...prev,
      [date]: !prev[date],
    }))
  }

  const formatDate = (date) =>
    new Intl.DateTimeFormat("en-IN", {
      day: "numeric",
      month: "short",
      year: "numeric",
    }).format(new Date(date))

  return (
    <div className="min-h-screen bg-green-50">
      <Navbar />
      <div className="container mx-auto px-4 py-8">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-2xl md:text-3xl font-bold text-gray-800">Marketplace</h1>
          {!showForm && !previewData && (
            <button
              onClick={() => setShowForm(true)}
              className="bg-green-700 hover:bg-green-600 text-white font-bold py-2 px-4 rounded-lg flex items-center transition duration-300"
            >
              <FaPlus className="mr-2" /> Add Crop Rate
            </button>
          )}
        </div>

        {success && (
          <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4 flex justify-between items-center">
            {success}
            <button onClick={() => setSuccess("")} className="text-green-700 font-bold ml-4">×</button>
          </div>
        )}

        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4 flex justify-between items-center">
            {error}
            <button onClick={() => setError("")} className="text-red-700 font-bold ml-4">×</button>
          </div>
        )}

        {/* Form */}
        {showForm && (
          <div className="bg-white rounded-lg shadow-md p-6 mb-8">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">Add New Crop Rate</h2>
            <form onSubmit={handleSubmit}>
              <div className="grid md:grid-cols-2 gap-4">
                <inputField label="Crop Name" name="cropName" value={formData.cropName} onChange={handleChange} />
                <inputField label="Location" name="location" value={formData.location} onChange={handleChange} />
                <div className="md:col-span-2">
                  <label className="block mb-2 text-sm font-bold text-gray-700">Pricing</label>
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    {["Maxprice", "Minprice", "Avgprice"].map((field) => (
                      <input
                        key={field}
                        name={field}
                        type="number"
                        step="0.01"
                        className="w-full border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-green-500"
                        placeholder={field}
                        value={formData[field]}
                        onChange={handleChange}
                      />
                    ))}
                  </div>
                </div>
                <div>
                  <label className="block mb-2 text-sm font-bold text-gray-700">Date</label>
                  <input
                    name="date"
                    type="date"
                    value={formData.date}
                    onChange={handleChange}
                    className="w-full border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-green-500"
                  />
                </div>
              </div>

              <div className="flex justify-between mt-6">
                <button type="button" onClick={handleCancelForm} className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded">Cancel</button>
                <button type="submit" className="bg-green-700 hover:bg-green-600 text-white font-bold py-2 px-4 rounded">Preview</button>
              </div>
            </form>
          </div>
        )}

        {/* Preview before POST */}
        {previewData && (
          <div className="bg-white rounded-lg shadow-md p-6 mb-8 border-2 border-green-500">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">Confirm Crop Rate Details</h2>
            <div className="grid md:grid-cols-2 gap-4 mb-6">
              {[
                ["Crop", previewData.cropName],
                ["MaxPrice", `₹${previewData.Maxprice}`],
                ["MinPrice", `₹${previewData.Minprice}`],
                ["AvgPrice", `₹${previewData.Avgprice}`],
                ["Location", previewData.location],
                ["Date", formatDate(previewData.date)],
                ["Added By", previewData.admin?.name || "Unknown"]
              ].map(([label, value]) => (
                <div key={label}><p className="text-sm text-gray-500">{label}</p><p className="font-medium">{value}</p></div>
              ))}
            </div>
            <div className="flex justify-between">
              <button onClick={cancelSubmission} className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded flex items-center"><FaTimes className="mr-2" /> Cancel</button>
              <button onClick={confirmSubmission} disabled={submitting} className={`bg-green-700 hover:bg-green-600 text-white font-bold py-2 px-4 rounded flex items-center ${submitting ? "opacity-50 cursor-not-allowed" : ""}`}>
                {submitting ? "Saving..." : (<><FaCheck className="mr-2" /> Confirm & Save</>)}
              </button>
            </div>
          </div>
        )}

        {/* Display Crops */}
        <div className="bg-white rounded-lg shadow-md overflow-hidden">
          <h2 className="text-xl font-semibold text-gray-800 p-6 border-b">Current Crop Rates</h2>
          {loading ? (
            <div className="p-6 text-center">Loading crop rates...</div>
          ) : crops.length === 0 ? (
            <div className="p-6 text-center">No crop rates available.</div>
          ) : (
            <div className="divide-y">
              {Object.entries(
                crops.reduce((grouped, crop) => {
                  const date = formatDate(crop.date)
                  if (!grouped[date]) grouped[date] = []
                  grouped[date].push(crop)
                  return grouped
                }, {})
              )
                .sort(([a], [b]) => new Date(b) - new Date(a))
                .map(([date, cropsOnDate]) => (
                  <div key={date} className="border-b">
                    <button
                      onClick={() => toggleDate(date)}
                      className="w-full text-left px-6 py-4 bg-green-100 hover:bg-green-200 font-semibold text-green-800 flex justify-between items-center"
                    >
                      <span>{date}</span>
                      <span>{expandedDates[date] ? "▲" : "▼"}</span>
                    </button>
                    {expandedDates[date] && (
                      <div className="overflow-x-auto">
                        <table className="w-full">
                          <thead className="bg-green-50">
                            <tr>
                              {["Crop", "MaxPrice", "MinPrice", "AvgPrice", "Location", "Added By"].map((label) => (
                                <th key={label} className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">{label}</th>
                              ))}
                            </tr>
                          </thead>
                          <tbody className="divide-y divide-gray-200">
                            {cropsOnDate.map((crop) => (
                              <tr key={crop._id} className="hover:bg-green-50">
                                <td className="px-6 py-4">{crop.cropName}</td>
                                <td className="px-6 py-4">₹{crop.Maxprice}</td>
                                <td className="px-6 py-4">₹{crop.Minprice}</td>
                                <td className="px-6 py-4">₹{crop.Avgprice}</td>
                                <td className="px-6 py-4">{crop.location}</td>
                                <td className="px-6 py-4">{crop.admin?.name || "Unknown"}</td>
                              </tr>
                            ))}
                          </tbody>
                        </table>
                      </div>
                    )}
                  </div>
                ))}
            </div>
          )}
        </div>
      </div>
    </div>
  )
}

export default Marketplace
